import React from "react";
import styled from "styled-components";

const StyledAudio = styled.audio`
  width: 100%;
  max-width: 500px;
  margin: 0 auto;
  display: block;
`;

const SongPlayer = ({ songUrl }) => {
    return (
        <div>
            {/* Display a loading message while the song URL is being fetched */}
            {!songUrl && <p>Loading...</p>}
            {/* Render the audio player if the song URL is available */}
            {songUrl && (
                <div>
                    <h1>Now Playing: Song Title</h1>
                    <StyledAudio controls>
                        <source src={songUrl} type="audio/mp3" />
                        Your browser does not support the audio element.
                    </StyledAudio>
                </div>
            )}
        </div>
    );
};

export default SongPlayer;
