import React, { useState } from 'react';
import axios from 'axios';

const CreateSongForm = () => {
    const [title, setTitle] = useState('');
    const [artist, setArtist] = useState('');
    const [album, setAlbum] = useState('');
    const [genre, setGenre] = useState('');
    const [songFile, setSongFile] = useState(null);
    const [imageFile, setImageFile] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        const formData = new FormData();
        formData.append('songFile', songFile);
        formData.append('imageFile', imageFile);
        formData.append('title', title);
        formData.append('artist', artist);
        formData.append('album', album);
        formData.append('genre', genre);

        try {
            const response = await axios.post('http://localhost:8083/songs', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            console.log('Song created successfully:', response.data);
            setIsLoading(false);
            setErrorMessage('');
            // Handle success or display a success message to the user
        } catch (error) {
            setIsLoading(false);
            if (error.response && error.response.data) {
                setErrorMessage(error.response.data);
            } else {
                setErrorMessage('Failed to create song. Please try again later.');
            }
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" />
            <input type="text" value={artist} onChange={(e) => setArtist(e.target.value)} placeholder="Artist" />
            <input type="text" value={album} onChange={(e) => setAlbum(e.target.value)} placeholder="Album" />
            <input type="text" value={genre} onChange={(e) => setGenre(e.target.value)} placeholder="Genre" />
            <label htmlFor="songFile">Select Song File:</label>
            <input type="file" onChange={(e) => setSongFile(e.target.files[0])} />
            <input type="file" onChange={(e) => setImageFile(e.target.files[0])} />
            <button type="submit" disabled={isLoading}>
                {isLoading ? 'Creating...' : 'Create Song'}
            </button>
            {errorMessage && <p>{errorMessage}</p>}
        </form>
    );
};

export default CreateSongForm;
