import * as React from 'react';

import { ToastContainer, toast } from 'react-toastify';
import { callApi } from '../helpers/axios_helper';
import { getUser, login, logout } from '../helpers/auth_helper';

import AuthContent from './AuthContent';
import Buttons from './Buttons';

export default class AppContent extends React.Component {

    constructor(props) {
        super(props);
        this.state = { user: {}, api: "" };
        this.shouldCancel = false;
    }

    componentDidMount() {
        this.getUser();
    }

    login = () => {
        login();
    };

    callApi = () => {
        callApi()
            .then(response => {
                this.setState({ api: response.data });
                toast.success('Api return successfully data, check in section - Api response');
            })
            .catch(error => {
                toast.error(error);
            });
    };

    componentWillUnmount() {
        this.shouldCancel = true;
    }

    logout = () => {
        logout();
    };

    getUser = () => {
        getUser().then(user => {
            if (user) {
                toast.success('User has been successfully loaded from store.');
            } else {
                toast.info('You are not logged in.');
            }

            if (!this.shouldCancel) {
                this.setState({ user });
            }
        });
    };

  render() {
    return (
      <>
        <ToastContainer />

        <Buttons
          login={this.login}
          logout={this.logout}
          getUser={this.getUser}
          callApi={this.callApi}
        />
          <div>
              <audio controls>
                  <source src="http://d2iujsfqj92c6r.cloudfront.net/ResonanceReversed.mp3?Expires=1693154134&Signature=GYjsIief78Wc~hkwcnpu6AIKoz2bDWoGHnJAkE-srtmRvy4WBA3S3DYbgBfp9n6VsDbXCBMvwBVWK34SSAlf1N4XZsBvay7E4HSMiMp-bHlUruw6ZMOhNULKk4XiPNhSraKo6OQGTfS~0DYmq0F4a0dgGQHjELsJOXL358NtaVBmpxa49IRbkOLnr5anjMMXgCl76NFBjozP2YsX8xlZTPrA~m1PFJFRirB6n42DTbF5TRcwKvrZbxvTZIIzJ~x84xva7X2JBg2uNx01umSGkKO6X91U8A9qJuhHVyr6iP4bFY1Ra7~UYOwKx0PWh0DuwESx~Q8SreYsZouripE4sQ__&Key-Pair-Id=APKA43UPJH7H766E6EMC" type="audio/ogg" />
              </audio>
          </div>
        {this.state.api && <AuthContent data={this.state.api} user={this.state.user} />}
      </>
    );
  }
}
