import React, { useState } from 'react';
import {getUser} from "../helpers/auth_helper";
import axios from "axios";

const MultiMatchSearch = () => {
    const [query, setQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const handleSearch = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            const response = await axios.get(`/your-server-endpoint/multi-match?query=${query}`);
            setSearchResults(response.data); // Update search results state
            setIsLoading(false);
            setErrorMessage('');
        } catch (error) {
            setIsLoading(false);
            if (error.response && error.response.data) {
                setErrorMessage(error.response.data);
            } else {
                setErrorMessage('Failed to perform the search. Please try again later.');
            }
        }
    };

    return (
        <div>
            <h1>Multi-Match Search</h1>
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    placeholder="Enter your search query"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
                <button type="submit" disabled={isLoading}>
                    {isLoading ? 'Searching...' : 'Search'}
                </button>
            </form>
            {errorMessage && <p>{errorMessage}</p>}
            <div>
                {/* Display search results here */}
                {searchResults.map((result, index) => (
                    <SearchResult key={index} result={result} />
                ))}
            </div>
        </div>
    );
};

const SearchResult = ({ result }) => {
    return (
        <div className="search-result">
            <h2>{result.title}</h2>
            <p>Artist: {result.artist}</p>
            <p>Album: {result.album}</p>
            {/* Add more details as needed */}
        </div>
    );
};

export default MultiMatchSearch;
