import React, { useState } from 'react';
import {getUser} from "../helpers/auth_helper";
import axios from "axios";

const MultiMatchSearch = () => {
    const [query, setQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const handleSearch = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            const response = await callApiPost({ query }); // Call the API with the user's query
            setSearchResults(response.data); // Update search results state
            setIsLoading(false);
            setErrorMessage('');
        } catch (error) {
            setIsLoading(false);
            if (error.response && error.response.data) {
                setErrorMessage(error.response.data);
            } else {
                setErrorMessage('Failed to perform the search. Please try again later.');
            }
        }
    };

    return (
        <div>
            <h1>Multi-Match Search</h1>
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    placeholder="Enter your search query"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
                <button type="submit" disabled={isLoading}>
                    {isLoading ? 'Searching...' : 'Search'}
                </button>
            </form>
            {errorMessage && <p>{errorMessage}</p>}
            <div>
                {/* Display search results here */}
                {searchResults.map((result, index) => (
                    <div key={index}>
                        {/* Render each search result */}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MultiMatchSearch;

const callApiPost = (data) => {
    return getUser().then((user) => {
        if (user && user.access_token) {
            const headers = {
                'Content-Type': 'multipart/form-data',
                Authorization: `Bearer ${user.access_token}`,
            };
            return axios.post('http://localhost:8083/songs', data, { headers });
        } else {
            throw new Error('User is not logged in');
        }
    });
};
